import React from 'react';
import { useQuery } from 'react-query';
import { Link } from 'react-router-dom';
import { 
  CubeIcon, 
  TagIcon, 
  ArchiveBoxIcon,
  BuildingOfficeIcon,
  MapPinIcon,
  ClipboardDocumentListIcon,
  BellIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  XCircleIcon,
  ArrowTrendingUpIcon,
  CurrencyDollarIcon,
  ChartBarIcon,
  ChartPieIcon
} from '@heroicons/react/24/outline';
import {
  PieChart,
  Pie,
  Cell,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  LineChart,
  Line,
  AreaChart,
  Area,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  ComposedChart,
  Scatter,
  ScatterChart,
  ZAxis
} from 'recharts';
import apiService from '../services/api';
import AdvancedCharts from '../components/AdvancedCharts';

const Dashboard: React.FC = () => {
  const { data: productsData } = useQuery(['products'], () => apiService.products.getProducts());
  const { data: categoriesData } = useQuery(['categories'], () => apiService.categories.getCategories());
  const { data: inventoryData } = useQuery(['inventory'], () => apiService.inventory.getInventory());
  const { data: suppliersData } = useQuery(['suppliers'], () => apiService.suppliers.getSuppliers());
  const { data: locationsData } = useQuery(['locations'], () => apiService.locations.getLocations());
  const { data: purchaseOrdersData } = useQuery(['purchase-orders'], () => apiService.purchaseOrders.getPurchaseOrders());
  const { data: activeAlertsData } = useQuery(['active-alerts'], () => apiService.stockAlerts.getActiveAlerts());

  const products = productsData?.data || [];
  const categories = categoriesData?.data || [];
  const inventory = inventoryData?.data || [];
  const suppliers = suppliersData?.data || [];
  const locations = locationsData?.data || [];
  const purchaseOrders = purchaseOrdersData?.data || [];
  const activeAlerts = activeAlertsData || [];

  const lowStockItems = inventory.filter(item => {
    const product = products.find(p => p.id === item.product_id);
    return item.quantity > 0 && item.quantity <= (product?.min_stock_level || 0);
  });

  const outOfStockItems = inventory.filter(item => item.quantity === 0);

  const pendingPOs = purchaseOrders.filter(po => 
    po.status === 'pending_approval' || po.status === 'approved'
  );

  const recentPOs = purchaseOrders.slice(0, 5);

  // Enhanced chart data preparation with better colors
  const categoryData = categories.map((category, index) => {
    const categoryProducts = products.filter(product => product.category_id === category.id);
    const totalValue = categoryProducts.reduce((sum, product) => {
      const inventoryItems = inventory.filter(item => item.product_id === product.id);
      const stockValue = inventoryItems.reduce((itemSum, item) => itemSum + (item.quantity * product.price), 0);
      return sum + stockValue;
    }, 0);

    return {
      name: category.name,
      value: totalValue,
      count: categoryProducts.length,
      fill: getCategoryColor(index)
    };
  });

  const stockStatusData = [
    { name: 'In Stock', value: inventory.filter(item => item.quantity > 0).length, fill: '#10B981', stroke: '#059669' },
    { name: 'Low Stock', value: lowStockItems.length, fill: '#F59E0B', stroke: '#D97706' },
    { name: 'Out of Stock', value: outOfStockItems.length, fill: '#EF4444', stroke: '#DC2626' }
  ];

  const poStatusData = [
    { name: 'Draft', value: purchaseOrders.filter(po => po.status === 'draft').length, fill: '#6B7280' },
    { name: 'Pending', value: purchaseOrders.filter(po => po.status === 'pending_approval').length, fill: '#F59E0B' },
    { name: 'Approved', value: purchaseOrders.filter(po => po.status === 'approved').length, fill: '#3B82F6' },
    { name: 'Ordered', value: purchaseOrders.filter(po => po.status === 'ordered').length, fill: '#8B5CF6' },
    { name: 'Received', value: purchaseOrders.filter(po => po.status === 'received').length, fill: '#10B981' },
    { name: 'Cancelled', value: purchaseOrders.filter(po => po.status === 'cancelled').length, fill: '#EF4444' }
  ];

  const monthlyData = generateMonthlyData(purchaseOrders);
  const topSuppliersData = generateTopSuppliersData(purchaseOrders, suppliers);
  const inventoryValueData = generateInventoryValueData(inventory, products);
  const categoryPerformanceData = generateCategoryPerformanceData(categories, products, inventory);
  const stockMovementData = generateStockMovementData(inventory, products);

  const stats = [
    {
      name: 'Total Products',
      value: products.length,
      icon: CubeIcon,
      color: 'bg-blue-500',
      href: '/products',
      trend: '+12%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Categories',
      value: categories.length,
      icon: TagIcon,
      color: 'bg-green-500',
      href: '/categories',
      trend: '+5%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Inventory Items',
      value: inventory.length,
      icon: ArchiveBoxIcon,
      color: 'bg-purple-500',
      href: '/inventory',
      trend: '+8%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Suppliers',
      value: suppliers.length,
      icon: BuildingOfficeIcon,
      color: 'bg-indigo-500',
      href: '/suppliers',
      trend: '+3%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Locations',
      value: locations.length,
      icon: MapPinIcon,
      color: 'bg-yellow-500',
      href: '/locations',
      trend: '+2%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Purchase Orders',
      value: purchaseOrders.length,
      icon: ClipboardDocumentListIcon,
      color: 'bg-pink-500',
      href: '/purchase-orders',
      trend: '+15%',
      trendColor: 'text-green-600'
    }
  ];

  const alerts = [
    {
      name: 'Active Alerts',
      value: activeAlerts.length,
      icon: BellIcon,
      color: 'bg-red-500',
      href: '/stock-alerts'
    },
    {
      name: 'Low Stock Items',
      value: lowStockItems.length,
      icon: ExclamationTriangleIcon,
      color: 'bg-yellow-500',
      href: '/inventory'
    },
    {
      name: 'Out of Stock',
      value: outOfStockItems.length,
      icon: XCircleIcon,
      color: 'bg-red-600',
      href: '/inventory'
    },
    {
      name: 'Pending POs',
      value: pendingPOs.length,
      icon: ClipboardDocumentListIcon,
      color: 'bg-orange-500',
      href: '/purchase-orders'
    }
  ];

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          <p className="text-gray-600">Welcome to your Inventory Management System</p>
        </div>
        <div className="flex items-center space-x-2">
          <ChartBarIcon className="h-6 w-6 text-blue-600" />
          <span className="text-sm text-gray-500">Real-time Analytics</span>
        </div>
      </div>

      {/* Enhanced Main Stats with Trends */}
