// Enhanced helper functions
function getCategoryColor(index: number) {
  const colors = [
    '#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6',
    '#06B6D4', '#84CC16', '#F97316', '#EC4899', '#6366F1',
    '#14B8A6', '#F43F5E', '#A855F7', '#06B6D4', '#84CC16'
  ];
  return colors[index % colors.length];
}

function generateMonthlyData(purchaseOrders: any[]) {
  const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
  const currentYear = new Date().getFullYear();

  return months.map((month, index) => {
    const monthOrders = purchaseOrders.filter(po => {
      const poDate = new Date(po.created_at);
      return poDate.getFullYear() === currentYear && poDate.getMonth() === index;
    });

    return {
      month,
      orders: monthOrders.length,
      value: monthOrders.reduce((sum, po) => sum + (po.total_amount || 0), 0)
    };
  });
}

function generateTopSuppliersData(purchaseOrders: any[], suppliers: any[]) {
  const supplierOrderCounts = purchaseOrders.reduce((acc, po) => {
    const supplierId = po.supplier_id;
    if (supplierId) {
      acc[supplierId] = (acc[supplierId] || 0) + 1;
    }
    return acc;
  }, {} as Record<number, number>);

  return Object.entries(supplierOrderCounts)
    .map(([supplierId, orders]) => {
      const supplier = suppliers.find(s => s.id === parseInt(supplierId));
      return {
        name: supplier?.name || `Supplier ${supplierId}`,
        orders
      };
    })
    .sort((a, b) => b.orders - a.orders)
    .slice(0, 10);
}

function generateInventoryValueData(inventory: any[], products: any[]) {
  return inventory.map(item => {
    const product = products.find(p => p.id === item.product_id);
    return {
      name: product?.name || `Product ${item.product_id}`,
      value: (item.quantity || 0) * (product?.price || 0)
    };
  }).sort((a, b) => b.value - a.value).slice(0, 10);
}

function generateCategoryPerformanceData(categories: any[], products: any[], inventory: any[]) {
  return categories.map(category => {
    const categoryProducts = products.filter(product => product.category_id === category.id);
    const totalValue = categoryProducts.reduce((sum, product) => {
      const inventoryItems = inventory.filter(item => item.product_id === product.id);
      const stockValue = inventoryItems.reduce((itemSum, item) => itemSum + (item.quantity * product.price), 0);
      return sum + stockValue;
    }, 0);

    return {
      category: category.name,
      products: categoryProducts.length,
      value: totalValue
    };
  });
}

function generateStockMovementData(inventory: any[], products: any[]) {
  return inventory.map(item => {
    const product = products.find(p => p.id === item.product_id);
    return {
      name: product?.name || `Product ${item.product_id}`,
      price: product?.price || 0,
      quantity: item.quantity || 0,
      value: (item.quantity || 0) * (product?.price || 0)
    };
  });
}

