import React from 'react';
import { useQuery } from 'react-query';
import { Link } from 'react-router-dom';
import { 
  CubeIcon, 
  TagIcon, 
  ArchiveBoxIcon,
  BuildingOfficeIcon,
  MapPinIcon,
  ClipboardDocumentListIcon,
  BellIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  XCircleIcon,
  ArrowTrendingUpIcon,
  CurrencyDollarIcon,
  ChartBarIcon,
  ChartPieIcon
} from '@heroicons/react/24/outline';
import {
  PieChart,
  Pie,
  Cell,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  LineChart,
  Line,
  AreaChart,
  Area,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  ComposedChart,
  Scatter,
  ScatterChart,
  ZAxis
} from 'recharts';
import apiService from '../services/api';
import AdvancedCharts from '../components/AdvancedCharts';

const Dashboard: React.FC = () => {
  const { data: productsData } = useQuery(['products'], () => apiService.products.getProducts());
  const { data: categoriesData } = useQuery(['categories'], () => apiService.categories.getCategories());
  const { data: inventoryData } = useQuery(['inventory'], () => apiService.inventory.getInventory());
  const { data: suppliersData } = useQuery(['suppliers'], () => apiService.suppliers.getSuppliers());
  const { data: locationsData } = useQuery(['locations'], () => apiService.locations.getLocations());
  const { data: purchaseOrdersData } = useQuery(['purchase-orders'], () => apiService.purchaseOrders.getPurchaseOrders());
  const { data: activeAlertsData } = useQuery(['active-alerts'], () => apiService.stockAlerts.getActiveAlerts());

  const products = productsData?.data || [];
  const categories = categoriesData?.data || [];
  const inventory = inventoryData?.data || [];
  const suppliers = suppliersData?.data || [];
  const locations = locationsData?.data || [];
  const purchaseOrders = purchaseOrdersData?.data || [];
  const activeAlerts = activeAlertsData || [];

  const lowStockItems = inventory.filter(item => {
    const product = products.find(p => p.id === item.product_id);
    return item.quantity > 0 && item.quantity <= (product?.min_stock_level || 0);
  });

  const outOfStockItems = inventory.filter(item => item.quantity === 0);

  const pendingPOs = purchaseOrders.filter(po => 
    po.status === 'pending_approval' || po.status === 'approved'
  );

  const recentPOs = purchaseOrders.slice(0, 5);

  // Enhanced chart data preparation with better colors
  const categoryData = categories.map((category, index) => {
    const categoryProducts = products.filter(product => product.category_id === category.id);
    const totalValue = categoryProducts.reduce((sum, product) => {
      const inventoryItems = inventory.filter(item => item.product_id === product.id);
      const stockValue = inventoryItems.reduce((itemSum, item) => itemSum + (item.quantity * product.price), 0);
      return sum + stockValue;
    }, 0);

    return {
      name: category.name,
      value: totalValue,
      count: categoryProducts.length,
      fill: getCategoryColor(index)
    };
  });

  const stockStatusData = [
    { name: 'In Stock', value: inventory.filter(item => item.quantity > 0).length, fill: '#10B981', stroke: '#059669' },
    { name: 'Low Stock', value: lowStockItems.length, fill: '#F59E0B', stroke: '#D97706' },
    { name: 'Out of Stock', value: outOfStockItems.length, fill: '#EF4444', stroke: '#DC2626' }
  ];

  const poStatusData = [
    { name: 'Draft', value: purchaseOrders.filter(po => po.status === 'draft').length, fill: '#6B7280' },
    { name: 'Pending', value: purchaseOrders.filter(po => po.status === 'pending_approval').length, fill: '#F59E0B' },
    { name: 'Approved', value: purchaseOrders.filter(po => po.status === 'approved').length, fill: '#3B82F6' },
    { name: 'Ordered', value: purchaseOrders.filter(po => po.status === 'ordered').length, fill: '#8B5CF6' },
    { name: 'Received', value: purchaseOrders.filter(po => po.status === 'received').length, fill: '#10B981' },
    { name: 'Cancelled', value: purchaseOrders.filter(po => po.status === 'cancelled').length, fill: '#EF4444' }
  ];

  const monthlyData = generateMonthlyData(purchaseOrders);
  const topSuppliersData = generateTopSuppliersData(purchaseOrders, suppliers);
  const inventoryValueData = generateInventoryValueData(inventory, products);
  const categoryPerformanceData = generateCategoryPerformanceData(categories, products, inventory);
  const stockMovementData = generateStockMovementData(inventory, products);

  const stats = [
    {
      name: 'Total Products',
      value: products.length,
      icon: CubeIcon,
      color: 'bg-blue-500',
      href: '/products',
      trend: '+12%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Categories',
      value: categories.length,
      icon: TagIcon,
      color: 'bg-green-500',
      href: '/categories',
      trend: '+5%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Inventory Items',
      value: inventory.length,
      icon: ArchiveBoxIcon,
      color: 'bg-purple-500',
      href: '/inventory',
      trend: '+8%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Suppliers',
      value: suppliers.length,
      icon: BuildingOfficeIcon,
      color: 'bg-indigo-500',
      href: '/suppliers',
      trend: '+3%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Locations',
      value: locations.length,
      icon: MapPinIcon,
      color: 'bg-yellow-500',
      href: '/locations',
      trend: '+2%',
      trendColor: 'text-green-600'
    },
    {
      name: 'Purchase Orders',
      value: purchaseOrders.length,
      icon: ClipboardDocumentListIcon,
      color: 'bg-pink-500',
      href: '/purchase-orders',
      trend: '+15%',
      trendColor: 'text-green-600'
    }
  ];

  const alerts = [
    {
      name: 'Active Alerts',
      value: activeAlerts.length,
      icon: BellIcon,
      color: 'bg-red-500',
      href: '/stock-alerts'
    },
    {
      name: 'Low Stock Items',
      value: lowStockItems.length,
      icon: ExclamationTriangleIcon,
      color: 'bg-yellow-500',
      href: '/inventory'
    },
    {
      name: 'Out of Stock',
      value: outOfStockItems.length,
      icon: XCircleIcon,
      color: 'bg-red-600',
      href: '/inventory'
    },
    {
      name: 'Pending POs',
      value: pendingPOs.length,
      icon: ClipboardDocumentListIcon,
      color: 'bg-orange-500',
      href: '/purchase-orders'
    }
  ];

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          <p className="text-gray-600">Welcome to your Inventory Management System</p>
        </div>
        <div className="flex items-center space-x-2">
          <ChartBarIcon className="h-6 w-6 text-blue-600" />
          <span className="text-sm text-gray-500">Real-time Analytics</span>
        </div>
      </div>

      {/* Enhanced Main Stats with Trends */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {stats.map((stat) => (
          <Link
            key={stat.name}
            to={stat.href}
            className="bg-white overflow-hidden shadow-lg rounded-xl hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1"
          >
            <div className="p-6">
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <div className={`flex-shrink-0 p-3 rounded-xl ${stat.color} shadow-lg`}>
                    <stat.icon className="h-6 w-6 text-white" />
                  </div>
                  <div className="ml-4">
                    <dt className="text-sm font-medium text-gray-500 truncate">{stat.name}</dt>
                    <dd className="text-2xl font-bold text-gray-900">{stat.value}</dd>
                  </div>
                </div>
                <div className="text-right">
                  <span className={`text-sm font-semibold ${stat.trendColor}`}>
                    {stat.trend}
                  </span>
                  <ArrowTrendingUpIcon className="h-4 w-4 text-green-500 ml-1" />
                </div>
              </div>
            </div>
          </Link>
        ))}
      </div>

      {/* Enhanced Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Animated Stock Status Donut Chart */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <div className="px-6 py-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">Stock Status Overview</h3>
              <ChartPieIcon className="h-6 w-6 text-blue-600" />
            </div>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={stockStatusData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name, percent }) => `${name} ${((percent || 0) * 100).toFixed(0)}%`}
                  outerRadius={100}
                  innerRadius={60}
                  fill="#8884d8"
                  dataKey="value"
                  animationDuration={1000}
                  animationBegin={0}
                >
                  {stockStatusData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.fill} stroke={entry.stroke} strokeWidth={2} />
                  ))}
                </Pie>
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '8px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                  }}
                />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Enhanced Purchase Order Status Bar Chart */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <div className="px-6 py-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">Purchase Order Status</h3>
              <ChartBarIcon className="h-6 w-6 text-green-600" />
            </div>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={poStatusData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis dataKey="name" tick={{ fontSize: 12 }} />
                <YAxis tick={{ fontSize: 12 }} />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '8px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                  }}
                />
                <Bar dataKey="value" fill="#8884d8" radius={[4, 4, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>

      {/* Category Value and Inventory Value Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Enhanced Category Value Distribution */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <div className="px-6 py-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">Category Value Distribution</h3>
              <CurrencyDollarIcon className="h-6 w-6 text-purple-600" />
            </div>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={categoryData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name, value }) => `${name}: $${Number(value || 0).toFixed(2)}`}
                  outerRadius={100}
                  fill="#8884d8"
                  dataKey="value"
                  animationDuration={1000}
                  animationBegin={0}
                >
                  {categoryData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.fill} />
                  ))}
                </Pie>
                <Tooltip 
                  formatter={(value) => [`$${Number(value).toFixed(2)}`, 'Value']}
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '8px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                  }}
                />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Inventory Value Trend */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <div className="px-6 py-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">Inventory Value Trend</h3>
              <ArrowTrendingUpIcon className="h-6 w-6 text-green-600" />
            </div>
            <ResponsiveContainer width="100%" height={300}>
              <AreaChart data={inventoryValueData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis dataKey="name" tick={{ fontSize: 12 }} />
                <YAxis tick={{ fontSize: 12 }} />
                <Tooltip 
                  formatter={(value) => [`$${Number(value).toFixed(2)}`, 'Value']}
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '8px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                  }}
                />
                <Area 
                  type="monotone" 
                  dataKey="value" 
                  stroke="#10B981" 
                  fill="#10B981" 
                  fillOpacity={0.3}
                  strokeWidth={3}
                />
              </AreaChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>

      {/* New Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Category Performance Radar Chart */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <div className="px-6 py-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">Category Performance</h3>
              <ChartBarIcon className="h-6 w-6 text-indigo-600" />
            </div>
            <ResponsiveContainer width="100%" height={300}>
              <RadarChart data={categoryPerformanceData}>
                <PolarGrid stroke="#e5e7eb" />
                <PolarAngleAxis dataKey="category" tick={{ fontSize: 12 }} />
                <PolarRadiusAxis tick={{ fontSize: 12 }} />
                <Radar 
                  name="Products" 
                  dataKey="products" 
                  stroke="#3B82F6" 
                  fill="#3B82F6" 
                  fillOpacity={0.3}
                  strokeWidth={2}
                />
                <Radar 
                  name="Value" 
                  dataKey="value" 
                  stroke="#10B981" 
                  fill="#10B981" 
                  fillOpacity={0.3}
                  strokeWidth={2}
                />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '8px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                  }}
                />
                <Legend />
              </RadarChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Stock Movement Scatter Chart */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <div className="px-6 py-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">Stock Movement Analysis</h3>
              <ChartBarIcon className="h-6 w-6 text-pink-600" />
            </div>
            <ResponsiveContainer width="100%" height={300}>
              <ScatterChart data={stockMovementData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis 
                  type="number" 
                  dataKey="price" 
                  name="Price" 
                  tick={{ fontSize: 12 }}
                />
                <YAxis 
                  type="number" 
                  dataKey="quantity" 
                  name="Quantity" 
                  tick={{ fontSize: 12 }}
                />
                <ZAxis dataKey="value" range={[60, 400]} />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '8px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                  }}
                />
                <Scatter 
                  name="Stock Items" 
                  data={stockMovementData} 
                  fill="#8B5CF6"
                  stroke="#7C3AED"
                  strokeWidth={1}
                />
              </ScatterChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>

      {/* Monthly Trends and Top Suppliers */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Enhanced Monthly Purchase Order Trends */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <div className="px-6 py-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">Monthly Purchase Order Trends</h3>
              <ArrowTrendingUpIcon className="h-6 w-6 text-blue-600" />
            </div>
            <ResponsiveContainer width="100%" height={300}>
              <ComposedChart data={monthlyData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis dataKey="month" tick={{ fontSize: 12 }} />
                <YAxis yAxisId="left" tick={{ fontSize: 12 }} />
                <YAxis yAxisId="right" orientation="right" tick={{ fontSize: 12 }} />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '8px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                  }}
                />
                <Legend />
                <Bar yAxisId="left" dataKey="orders" fill="#3B82F6" radius={[4, 4, 0, 0]} />
                <Line yAxisId="right" type="monotone" dataKey="value" stroke="#10B981" strokeWidth={3} />
              </ComposedChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Enhanced Top Suppliers */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <div className="px-6 py-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">Top Suppliers by Order Volume</h3>
              <BuildingOfficeIcon className="h-6 w-6 text-indigo-600" />
            </div>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={topSuppliersData} layout="horizontal">
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis type="number" tick={{ fontSize: 12 }} />
                <YAxis dataKey="name" type="category" width={120} tick={{ fontSize: 12 }} />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '8px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                  }}
                />
                <Bar dataKey="orders" fill="#8B5CF6" radius={[0, 4, 4, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>

      {/* Recent Activity with Enhanced Design */}
      <div className="bg-white shadow-lg rounded-xl overflow-hidden">
        <div className="px-6 py-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-xl font-bold text-gray-900">Recent Activity</h3>
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span className="text-sm text-gray-500">Live Updates</span>
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="flex items-center justify-between p-4 bg-gradient-to-r from-blue-50 to-blue-100 rounded-xl border border-blue-200">
              <div className="flex items-center">
                <CubeIcon className="h-8 w-8 text-blue-600 mr-4" />
                <div>
                  <p className="text-lg font-bold text-gray-900">New Products</p>
                  <p className="text-sm text-gray-600">This month</p>
                </div>
              </div>
              <span className="text-2xl font-bold text-blue-600">
                {products.filter(p => new Date(p.created_at).getMonth() === new Date().getMonth()).length}
              </span>
            </div>

            <div className="flex items-center justify-between p-4 bg-gradient-to-r from-green-50 to-green-100 rounded-xl border border-green-200">
              <div className="flex items-center">
                <CheckCircleIcon className="h-8 w-8 text-green-600 mr-4" />
                <div>
                  <p className="text-lg font-bold text-gray-900">Orders Completed</p>
                  <p className="text-sm text-gray-600">This month</p>
                </div>
              </div>
              <span className="text-2xl font-bold text-green-600">
                {purchaseOrders.filter(po => po.status === 'received' && new Date(po.created_at).getMonth() === new Date().getMonth()).length}
              </span>
            </div>

            <div className="flex items-center justify-between p-4 bg-gradient-to-r from-yellow-50 to-yellow-100 rounded-xl border border-yellow-200">
              <div className="flex items-center">
                <ExclamationTriangleIcon className="h-8 w-8 text-yellow-600 mr-4" />
                <div>
                  <p className="text-lg font-bold text-gray-900">Low Stock Alerts</p>
                  <p className="text-sm text-gray-600">Active</p>
                </div>
              </div>
              <span className="text-2xl font-bold text-yellow-600">{lowStockItems.length}</span>
            </div>
          </div>
        </div>
      </div>

      {/* Enhanced Alerts Section */}
      <div className="bg-white shadow-lg rounded-xl overflow-hidden">
        <div className="px-6 py-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-xl font-bold text-gray-900">Alerts & Notifications</h3>
            <BellIcon className="h-6 w-6 text-red-500 animate-pulse" />
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {alerts.map((alert) => (
              <Link
                key={alert.name}
                to={alert.href}
                className="relative bg-white p-6 border-2 border-gray-200 rounded-xl hover:border-gray-300 hover:shadow-lg transition-all duration-300 transform hover:-translate-y-1"
              >
                <div className="flex items-center">
                  <div className={`flex-shrink-0 p-3 rounded-xl ${alert.color} shadow-lg`}>
                    <alert.icon className="h-6 w-6 text-white" />
                  </div>
                  <div className="ml-4">
                    <p className="text-lg font-bold text-gray-900">{alert.value}</p>
                    <p className="text-sm text-gray-500">{alert.name}</p>
                  </div>
                </div>
                {alert.value > 0 && (
                  <div className={`absolute -top-2 -right-2 h-8 w-8 rounded-full ${alert.color} flex items-center justify-center shadow-lg animate-bounce`}>
                    <span className="text-sm font-bold text-white">{alert.value}</span>
                  </div>
                )}
              </Link>
            ))}
          </div>
        </div>
      </div>

      {/* Enhanced Recent Data Sections */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Purchase Orders */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <div className="px-6 py-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-900">Recent Purchase Orders</h3>
              <Link
                to="/purchase-orders"
                className="text-sm text-blue-600 hover:text-blue-500 font-medium"
              >
                View all 
              </Link>
            </div>
            <div className="space-y-3">
              {recentPOs.length > 0 ? (
                recentPOs.map((po) => (
                  <div key={po.id} className="flex items-center justify-between p-4 bg-gray-50 rounded-xl hover:bg-gray-100 transition-colors">
                    <div>
                      <p className="text-sm font-bold text-gray-900">{po.po_number}</p>
                      <p className="text-xs text-gray-500">
                        {po.supplier?.name}  ${po.total_amount.toFixed(2)}
                      </p>
                    </div>
                    <span className={`inline-flex px-3 py-1 text-xs font-bold rounded-full ${
                      po.status === 'received' ? 'bg-green-100 text-green-800' :
                      po.status === 'ordered' ? 'bg-blue-100 text-blue-800' :
                      po.status === 'pending_approval' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
