import React from 'react';
import { useQuery } from 'react-query';
import { Link } from 'react-router-dom';
import { 
  CubeIcon, 
  TagIcon, 
  ArchiveBoxIcon,
  BuildingOfficeIcon,
  MapPinIcon,
  ClipboardDocumentListIcon,
  BellIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  XCircleIcon
} from '@heroicons/react/24/outline';
import apiService from '../services/api';

const Dashboard: React.FC = () => {
  const { data: productsData } = useQuery(['products'], () => apiService.products.getProducts());
  const { data: categoriesData } = useQuery(['categories'], () => apiService.categories.getCategories());
  const { data: inventoryData } = useQuery(['inventory'], () => apiService.inventory.getInventory());
  const { data: suppliersData } = useQuery(['suppliers'], () => apiService.suppliers.getSuppliers());
  const { data: locationsData } = useQuery(['locations'], () => apiService.locations.getLocations());
  const { data: purchaseOrdersData } = useQuery(['purchase-orders'], () => apiService.purchaseOrders.getPurchaseOrders());
  const { data: activeAlertsData } = useQuery(['active-alerts'], () => apiService.stockAlerts.getActiveAlerts());

  const products = productsData?.data || [];
  const categories = categoriesData?.data || [];
  const inventory = inventoryData?.data || [];
  const suppliers = suppliersData?.data || [];
  const locations = locationsData?.data || [];
  const purchaseOrders = purchaseOrdersData?.data || [];
  const activeAlerts = activeAlertsData || [];

  const lowStockItems = inventory.filter(item => {
    const product = products.find(p => p.id === item.product_id);
    return item.quantity > 0 && item.quantity <= (product?.min_stock_level || 0);
  });

  const outOfStockItems = inventory.filter(item => item.quantity === 0);

  const pendingPOs = purchaseOrders.filter(po => 
    po.status === 'pending_approval' || po.status === 'approved'
  );

  const recentPOs = purchaseOrders.slice(0, 5);

  const stats = [
    {
      name: 'Total Products',
      value: products.length,
      icon: CubeIcon,
      color: 'bg-blue-500',
      href: '/products'
    },
    {
      name: 'Categories',
      value: categories.length,
      icon: TagIcon,
      color: 'bg-green-500',
      href: '/categories'
    },
    {
      name: 'Inventory Items',
      value: inventory.length,
      icon: ArchiveBoxIcon,
      color: 'bg-purple-500',
      href: '/inventory'
    },
    {
      name: 'Suppliers',
      value: suppliers.length,
      icon: BuildingOfficeIcon,
      color: 'bg-indigo-500',
      href: '/suppliers'
    },
    {
      name: 'Locations',
      value: locations.length,
      icon: MapPinIcon,
      color: 'bg-yellow-500',
      href: '/locations'
    },
    {
      name: 'Purchase Orders',
      value: purchaseOrders.length,
      icon: ClipboardDocumentListIcon,
      color: 'bg-pink-500',
      href: '/purchase-orders'
    }
  ];

  const alerts = [
    {
      name: 'Active Alerts',
      value: activeAlerts.length,
      icon: BellIcon,
      color: 'bg-red-500',
      href: '/stock-alerts'
    },
    {
      name: 'Low Stock Items',
      value: lowStockItems.length,
      icon: ExclamationTriangleIcon,
      color: 'bg-yellow-500',
      href: '/inventory'
    },
    {
      name: 'Out of Stock',
      value: outOfStockItems.length,
      icon: XCircleIcon,
      color: 'bg-red-600',
      href: '/inventory'
    },
    {
      name: 'Pending POs',
      value: pendingPOs.length,
      icon: ClipboardDocumentListIcon,
      color: 'bg-orange-500',
      href: '/purchase-orders'
    }
  ];

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <p className="text-gray-600">Welcome to your Inventory Management System</p>
      </div>

      {/* Main Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {stats.map((stat) => (
          <Link
            key={stat.name}
            to={stat.href}
            className="bg-white overflow-hidden shadow rounded-lg hover:shadow-md transition-shadow"
          >
            <div className="p-5">
              <div className="flex items-center">
                <div className={`flex-shrink-0 p-3 rounded-md ${stat.color}`}>
                  <stat.icon className="h-6 w-6 text-white" />
                </div>
                <div className="ml-5 w-0 flex-1">
                  <dl>
                    <dt className="text-sm font-medium text-gray-500 truncate">{stat.name}</dt>
                    <dd className="text-lg font-medium text-gray-900">{stat.value}</dd>
                  </dl>
                </div>
              </div>
            </div>
          </Link>
        ))}
      </div>

      {/* Alerts Section */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">Alerts & Notifications</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {alerts.map((alert) => (
              <Link
                key={alert.name}
                to={alert.href}
                className="relative bg-white p-4 border border-gray-200 rounded-lg hover:border-gray-300 transition-colors"
              >
                <div className="flex items-center">
                  <div className={`flex-shrink-0 p-2 rounded-md ${alert.color}`}>
                    <alert.icon className="h-5 w-5 text-white" />
                  </div>
                  <div className="ml-3">
                    <p className="text-sm font-medium text-gray-900">{alert.value}</p>
                    <p className="text-xs text-gray-500">{alert.name}</p>
                  </div>
                </div>
                {alert.value > 0 && (
                  <div className={`absolute -top-2 -right-2 h-6 w-6 rounded-full ${alert.color} flex items-center justify-center`}>
                    <span className="text-xs font-medium text-white">{alert.value}</span>
                  </div>
                )}
              </Link>
            ))}
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Purchase Orders */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Recent Purchase Orders</h3>
              <Link
                to="/purchase-orders"
                className="text-sm text-blue-600 hover:text-blue-500"
              >
                View all
              </Link>
            </div>
            <div className="space-y-3">
              {recentPOs.length > 0 ? (
                recentPOs.map((po) => (
                  <div key={po.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-md">
                    <div>
                      <p className="text-sm font-medium text-gray-900">{po.po_number}</p>
                      <p className="text-xs text-gray-500">
                        {po.supplier?.name} • ${po.total_amount.toFixed(2)}
                      </p>
                    </div>
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      po.status === 'received' ? 'bg-green-100 text-green-800' :
                      po.status === 'ordered' ? 'bg-blue-100 text-blue-800' :
                      po.status === 'pending_approval' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {po.status.replace('_', ' ').toUpperCase()}
                    </span>
                  </div>
                ))
              ) : (
                <p className="text-sm text-gray-500">No recent purchase orders</p>
              )}
            </div>
          </div>
        </div>

        {/* Low Stock Items */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Low Stock Items</h3>
              <Link
                to="/inventory"
                className="text-sm text-blue-600 hover:text-blue-500"
              >
                View all
              </Link>
            </div>
            <div className="space-y-3">
              {lowStockItems.length > 0 ? (
                lowStockItems.slice(0, 5).map((item) => {
                  const product = products.find(p => p.id === item.product_id);
                  return (
                    <div key={item.id} className="flex items-center justify-between p-3 bg-yellow-50 rounded-md">
                      <div>
                        <p className="text-sm font-medium text-gray-900">
                          {product?.name || `Product ID: ${item.product_id}`}
                        </p>
                        <p className="text-xs text-gray-500">
                          {item.location?.name} • Qty: {item.quantity}
                        </p>
                      </div>
                      <ExclamationTriangleIcon className="h-4 w-4 text-yellow-500" />
                    </div>
                  );
                })
              ) : (
                <p className="text-sm text-gray-500">No low stock items</p>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">Quick Actions</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <Link
              to="/products"
              className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-gray-300 hover:bg-gray-50 transition-colors"
            >
              <CubeIcon className="h-8 w-8 text-blue-600 mb-2" />
              <span className="text-sm font-medium text-gray-900">Add Product</span>
            </Link>
            <Link
              to="/inventory"
              className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-gray-300 hover:bg-gray-50 transition-colors"
            >
              <ArchiveBoxIcon className="h-8 w-8 text-purple-600 mb-2" />
              <span className="text-sm font-medium text-gray-900">Update Stock</span>
            </Link>
            <Link
              to="/purchase-orders"
              className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-gray-300 hover:bg-gray-50 transition-colors"
            >
              <ClipboardDocumentListIcon className="h-8 w-8 text-pink-600 mb-2" />
              <span className="text-sm font-medium text-gray-900">Create PO</span>
            </Link>
            <Link
              to="/suppliers"
              className="flex flex-col items-center p-4 border border-gray-200 rounded-lg hover:border-gray-300 hover:bg-gray-50 transition-colors"
            >
              <BuildingOfficeIcon className="h-8 w-8 text-indigo-600 mb-2" />
              <span className="text-sm font-medium text-gray-900">Add Supplier</span>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
